<?php defined('BASEPATH') OR exit('No direct script access allowed');/** * email_templates Plugin * * Create lists of posts *  * */class Plugin_Email_templates extends Plugin{	/**	 * email_templates List	 *	 * Creates a list of email_templates posts	 *	 * Usage:	 * {pyro:email_templates:posts limit="5"}	 *	<h2>{pyro:title}</h2>	 *	{pyro:body}	 * {/pyro:email_templates:posts}	 *	 * @param	array	 * @return	array	 */	function posts($data = array())	{		$limit = $this->attribute('limit', 10);		$order = $this->attribute('order');				return $this->db			->where('status', 'live')			->limit($limit)			->get('email_templates')			->result_array();	}	}/* End of file plugin.php */